  div(imgheight, imgheight, imgwidth);
  copy(imgwidth, maxwidth);
);

if(imgheight GT maxheight,
  mul(imgwidth, imgwidth, maxheight);
  div(imgwidth, imgwidth, imgheight);
  copy(imgheight, maxheight);
);

